import 'package:get/get.dart';
import 'package:reto2/clases/productos.dart';

class controladorGeneral extends GetxController {
  final _posicionPagina = 0.obs;
  final pro = <productos>[].obs;
  @override
  void onInit() {
    pro.add(productos(
        1,
        "Kit kabuki Rosa Panton 7 Montoc",
        "data:image/jpeg;base64,",
        0,
        40000));
    pro.add(productos(
        2,
        "Labial liquido Ultramate Ame RebelBlackLabial ",
        "data:image/jpeg;base64,",
        0,
        22000));
    pro.add(productos(
        3,
        "Polvo facial suelto",
        "data:image/jpeg;base64,",
        0,
        21000));
    pro.add(productos(
        4,
        "Base fluida",
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQHEz3-Hfw-JL_b-nN8uY5NwbjUq4tHy7umSA&usqp=CAU",
        0,
        60000));
    pro.add(productos(
        5,
        "Corrector fluido Milani retouch+eraser",
        "data:image/jpeg;base64,",
        0,
        48000));

    // TODO: implement onInit
    super.onInit();
  }

  void cambiarposicion(int x) {
    _posicionPagina.value = x;
  }

  int get posicionPagina => _posicionPagina.value;

  void cambiarCantidad(int posicion, int valor) {
    print(
        "posicion = " + posicion.toString() + " | valor = " + valor.toString());
    pro[posicion].cantidad = valor;
  }

  int calcularTotal() {
    int total = 0;
    for (int i = 0; i < pro.length; i++) {
      total = total + pro[i].cantidad * pro[i].precio;
    }

    return total;
  }

  void limpiartodo() {
    for (int i = 0; i < pro.length; i++) {
      pro[i].cantidad = 0;
    }
    calcularTotal();
  }
}
